$(function () {
	const e = $("body").data("page"),
		t = "https://sonic.sega.jp/SonicXShadowGenerations/",
		o = "https://line.me/R/msg/text/?",
		n = "https://twitter.com/intent/tweet?text=",
		s = "https://www.facebook.com/share.php?u=" + t,
		r = "『ソニック × シャドウ ジェネレーションズ』公式サイト ",
		a = "%0D%0A";
	let c = "2つのタイトルがセットになったハイスピードアクションアドベンチャー　『ソニック × シャドウ ジェネレーションズ』！　好評発売中",
		l = "",
		i = "";
	if ("products" === e) l = "products.html", i = "PRODUCTS";
	else if ("video" === e) l = "video.html", i = "VIDEO";
	else if ("character" === e) l = "character.html", i = "CHARACTER";
	else if ("story" === e) l = "story.html", i = "STORY";
	else if ("features" === e) l = "features.html", i = "FEATURES";
	else if ("stage" === e) l = "stage.html", i = "STAGE";
	else if ("news" === e) {
		const e = $("body").data("newsurl"),
			t = $(".newsDetailTitleText").html();
		l = "news/" + e, c = t, i = "NEWS"
	}
	const h = n + encodeURIComponent(r + i) + a + encodeURIComponent(c) + a + encodeURIComponent(t + l),
		m = o + encodeURIComponent(r + i) + a + encodeURIComponent(c) + a + encodeURIComponent(t + l),
		p = s + l;
	setTimeout(() => {
		$(".snsFBUrl").attr("href", p), $(".snsLineUrl").attr("href", m), $(".snsXUrl").attr("href", h)
	}, 1e3)
});